function Intro(props) {
    const openForm = () => {
        let modal = document.getElementsByClassName("form-wrapper");
        modal.style.display = "block";
    }
    return (
        <>
        <div className="form-wrapper">
            <form>
                <h2>test</h2>
            </form>
        </div>
        <div className="intro">
            <div className="left-part">
                <h1>{props.profile.name}</h1>
                <p className="location">{props.profile.city}, {props.profile.country}</p>
                <p className="tagline">{props.profile.tagline}</p>
                <ul className="taglist">
                    {props.profile.tags.map((tag, i) => {
                        return (  
                            <li key={i}>#{tag}</li>
                        )
                    })}
                </ul>
            </div>
            <div className="center-part">
                <button onClick={openForm()} >Contact button</button>
            </div>
            <div className="right-part">
                <img src={`${process.env.PUBLIC_URL}/${props.index}.png`} alt={props.profile.name} />
            </div>
        </div>
        </>
    )
}

export default Intro;
