import { useParams, useRouteMatch } from "react-router-dom";

function Intro(props) {

    let { id } = useParams();
    let { url } = useRouteMatch();


    return (
        <div className="intro">
            <div className="left-part">
                <h1>{props.profile.name}</h1>
                <p className="location">{props.profile.city}, {props.profile.country}</p>
                <p className="tagline">{props.profile.tagline}</p>
                <ul className="taglist">
                    {props.profile.tags.map((tag, i) => {
                        return (  
                            <li key={i}>{tag}</li>
                        )
                    })}
                </ul>
            </div>
            <div className="center-part">
                <button>Contact button</button>
            </div>
            <div className="right-part">
                <img src="https://fakeimg.pl/200x200/?text=photo" alt="fake img"/>
                <img src={`${url}/${props.index}.png`} alt={props.profile.name} />
            </div>
        </div>
    )
}

export default Intro;
