import {Link} from "react-router-dom";
import Lightbox from "./Lightbox";

function Medias(props) {

    let medias = props.medias;
    let photoDisplayed = 0;
    let mediaFolder = props.name.split(" ")[0];
    console.log(medias);

    return(
        <>
        <div className="gallery">
            {
                medias.map((media, index)=> {
                    photoDisplayed++;
                    if(media.image) {
                        return (
                            <div key={index} className="media">
                                <Link to={'/profile/'+photograph.id} >
                                    <img src={process.env.PUBLIC_URL + '/' + mediaFolder + '/' +media.image} alt={media.image} />
                                </Link>
                            </div>
                        )
                    } else if(media.video) {
                        return (
                            <div key={index} className="media">
                                <video autoPlay muted controls height="300">
                                    <source src={process.env.PUBLIC_URL + '/' + mediaFolder + '/' + media.video} type="video/mp4" />
                                    Sorry, your browser doesn't support embedded videos.
                                </video>
                            </div>
                        )
                    }
                })
            }
        </div>
        <Lightbox />
        </>
    )
}

export default Medias;
