import { useState, useEffect } from "react";
import * as _ from "lodash";

function Gallery(props) {

    let medias = props.medias;
    let photoDisplayed = 0;
    const [filter, setFilter] = useState('likes');
    let items = [...medias.filter(media => media.photographerId == props.profileId)];
    console.log(items);

    useEffect(()=> {
        items = _.orderBy(items, [{filter}], 'desc'); 
    })

    return(
        <div className="gallery-wrapper">
            <div className="filter">
                <p>Trier par</p> 
                <select 
                    id="filter" 
                    value={filter} 
                    onChange={e => {
                        setFilter(e.target.value);
                        console.log(filter);
                    }}
                >
                    <option value='likes' defaultValue>Popularit√©</option>
                    <option value='date'>Date</option>
                    <option value='title'>Titre</option>
                </select>
            </div>
            <div className="gallery">
                {
                items.map((media, index)=> {
                    photoDisplayed++;
                    if(media.image) {
                        return (
                            <div key={index} className="media">
                                <img src={process.env.PUBLIC_URL + '/' + media.image} alt={media.image} />
                            </div>
                        )
                    } else if(media.video) {
                        return (
                            <div key={index} className="media">
                                <video autoPlay muted controls height="300">
                                    <source src={process.env.PUBLIC_URL + '/' + media.video} type="video/mp4" />
                                    Sorry, your browser doesn't support embedded videos.
                                </video>
                            </div>
                        )
                    }
                })}
            </div>
        </div>
    )
}

export default Gallery;
