function Intro(props) {
    return (
        <>
        <div id="form-wrapper">
            <form>      
                <input name="name" type="text" class="feedback-input" placeholder="Name" />   
                <input name="email" type="text" class="feedback-input" placeholder="Email" />
                <textarea name="text" class="feedback-input" placeholder="Comment"></textarea>
                <input type="submit" value="SUBMIT"/>
            </form>
        </div>
        <div className="intro">
            <div className="left-part">
                <h1>{props.profile.name}</h1>
                <p className="location">{props.profile.city}, {props.profile.country}</p>
                <p className="tagline">{props.profile.tagline}</p>
                <ul className="taglist">
                    {props.profile.tags.map((tag, i) => {
                        return (  
                            <li key={i}>#{tag}</li>
                        )
                    })}
                </ul>
            </div>
            <div className="center-part">
                <button onClick={() => {
                            let modal = document.getElementById("form-wrapper");
                            modal.style.display = "block";
                }}>
                    Contact button
                </button>
            </div>
            <div className="right-part">
                <img src={`${process.env.PUBLIC_URL}/${props.index}.png`} alt={props.profile.name} />
            </div>
        </div>
        </>
    )
}

export default Intro;
