import { indexOf } from "lodash";
import { useState } from "react";

function Medias(props) {

    let medias = props.medias;
    let mediaFolder = props.name.split(" ")[0];
    let currentIndex; 

    //State that defines whether the lightbox is displayed or not :
    const [lightboxDisplay, setLightBoxDisplay] = useState(false);
    //State that defines which image has to be displayed : 
    const [mediaToShow, setMediaToShow] = useState('');

    const showImage = (src) => {
        //set imageToShow to be the one that's been clicked on    
        setMediaToShow(src);
        //set lightbox visibility to true
        setLightBoxDisplay(true);
    };
    //Close the lightbox :
    const hideLightBox = () => {
        setLightBoxDisplay(false)
    }
    //Show next media :
    const showNext = (mediaSrc) => {
        console.log(mediaSrc);
        let currentMedia = document.querySelectorAll('.gallery-img');
        let arrayOfMedias = Array.from(currentMedia);
        let mediaDisplayed;
        let nextMedia;
        console.log(arrayOfMedias);
        arrayOfMedias.map((media)=> {
            if(media.attributes.src.nodeValue == mediaSrc) {
                mediaDisplayed = media;
                console.log(mediaDisplayed.id);
                let nextId = mediaDisplayed++;
                nextMedia = document.querySelector('#'+nextId);
                console.log("next media", nextMedia);
            }
        })
        //let nextMedia = medias[index == currentIndex]+1;
        /* console.log(nextMedia);
        setMediaToShow(nextMedia); */
    }

    return(
        <>
            <div className="gallery">
                {   
                    medias.map((media, index)=> {
                        if(media.image) {

                            return (
                                <div key={index} className="media">
                                    <img src={process.env.PUBLIC_URL + '/' + mediaFolder + '/' + media.image} alt={media.image} className="gallery-img" id={index}
                                        onClick={() => {
                                            showImage(process.env.PUBLIC_URL + '/' + mediaFolder + '/' + media.image);
                                            currentIndex = index;
                                            console.log('CurrentIndex : ');
                                        }} 
                                    />
                                </div>
                            )

                        } else if(media.video) {

                            return (
                                <div key={index} className="media">
                                    <video autoPlay muted controls height="300" onClick={() => {
                                            showImage(process.env.PUBLIC_URL + '/' + mediaFolder + '/' + media.video);
                                        }} 
                                    >
                                        <source src={process.env.PUBLIC_URL + '/' + mediaFolder + '/' + media.video} type="video/mp4" />
                                        Sorry, your browser doesn't support embedded videos.
                                    </video>
                                </div>
                            )

                        }
                    })
                }     
            </div>
            { lightboxDisplay ?
                <div id="lightbox" onClick={hideLightBox}>
                    <button>⭠</button>
                    <div className="media">
                        <img id="lightbox-img" src={mediaToShow} />
                    </div>
                    <button onClick={(e) => {
                                e.stopPropagation();
                                showNext(mediaToShow);
                            }} >
                        ⭢
                    </button>
                </div>
            : '' }
        </>
    )
}

export default Medias;
