import data from '../data/data';
import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import Photograph from './Photograph';

function Photographes() {

    const [photographers, setPhotographers] = useState(data.photographers);
    const [medias, setMedias] = useState(data.medias);
    const [index, setIndex] = useState(0);

    return (
        <Router>
            <main>
                <h1>Nos Photographes</h1>
                <section id="photographs">
                    {/* Faire un .map pour retourner un composant <Photograph/> pour chaque élément de l'objet photographs */}
                    {photographers.map((photograph, i) => {
                        return (
                            <div className="photograph" key={i}>
                                <div className="photograph-container">
                                    <img src={process.env.PUBLIC_URL + index + '.png'} alt={photograph.name} />
                                <Link to={'/profile/'+index}>
                                    <h2 className="name">{photograph.name}</h2>
                                </Link>
                                <p className="location">{photograph.city + ', ' + photograph.country}</p>
                                </div>
                            </div>
                        )  
                    })}
                </section>
            </main>
            <Switch>
                <Route path={'/profile/'+index}>
                    <Photograph />
                </Route>
            </Switch>
        </Router>
    )
}

export default Photographes;
